import{_ as e,c as i,o as n,a as t}from"./app.0db58bf6.js";const p=JSON.parse('{"title":"5e Hausregeln","description":"","frontmatter":{},"headers":[{"level":2,"title":"Character Creation","slug":"character-creation","link":"#character-creation","children":[{"level":3,"title":"Classes","slug":"classes","link":"#classes","children":[]},{"level":3,"title":"Origin: Race (UA Character Origin)","slug":"origin-race-ua-character-origin","link":"#origin-race-ua-character-origin","children":[]}]},{"level":2,"title":"Customization Options","slug":"customization-options","link":"#customization-options","children":[]},{"level":2,"title":"Playing the Game","slug":"playing-the-game","link":"#playing-the-game","children":[{"level":3,"title":"Ability Scores","slug":"ability-scores","link":"#ability-scores","children":[]},{"level":3,"title":"Resting","slug":"resting","link":"#resting","children":[]}]},{"level":2,"title":"Adventuring","slug":"adventuring","link":"#adventuring","children":[]},{"level":2,"title":"Combat","slug":"combat","link":"#combat","children":[]}],"relativePath":"rules/index.md"}'),r={name:"rules/index.md"},a=t('<h1 id="_5e-hausregeln" tabindex="-1">5e Hausregeln <a class="header-anchor" href="#_5e-hausregeln" aria-hidden="true">#</a></h1><p>House Rules - <em>Optional/Variant Rules</em></p><h2 id="character-creation" tabindex="-1">Character Creation <a class="header-anchor" href="#character-creation" aria-hidden="true">#</a></h2><h3 id="classes" tabindex="-1">Classes <a class="header-anchor" href="#classes" aria-hidden="true">#</a></h3><p><a href="./wizard.html">Wizard</a></p><h3 id="origin-race-ua-character-origin" tabindex="-1">Origin: Race (<a href="https://media.dndbeyond.com/compendium-images/one-dnd/character-origins/CSWCVV0M4B6vX6E1/UA2022-CharacterOrigins.pdf" target="_blank" rel="noreferrer">UA Character Origin</a>) <a class="header-anchor" href="#origin-race-ua-character-origin" aria-hidden="true">#</a></h3><ul><li>Races geben keine Stat-Boots mehr</li></ul><p><strong>e.g. Human:</strong></p><ul><li><strong>Resourceful</strong>. You gain Inspiration whenever you finish a Long Rest.</li><li><strong>Skillful</strong>. You gain Proficiency in one Skill of your choice.</li><li><strong>Versatile</strong>. You gain the Skilled Feat or another 1st-level Feat of your choice.</li></ul><p>Origin: Background (UA Character Origin)</p><ul><li><strong>Ability Scores</strong>. +2 / +1 oder +1 / +1 / +1</li><li><strong>Skill Proficiencies</strong>. Choose 2</li><li><strong>Tool Proficiency</strong>: Choose 1</li><li><strong>Language</strong>: Choose 1</li><li><strong>Feat</strong>: Choose 1 1st-Level Feat</li><li><strong>Equipment</strong>: 50gp worth of Equipment/Cash</li></ul><h2 id="customization-options" tabindex="-1">Customization Options <a class="header-anchor" href="#customization-options" aria-hidden="true">#</a></h2><ul><li><s>Feats (PHB 165)</s></li><li><em>Multiclassing (PHB 163)</em></li><li><em>Sidekicks (TCE 142), in Kombination mit Retainer aus Strongholds &amp; Followers</em></li></ul><h2 id="playing-the-game" tabindex="-1">Playing the Game <a class="header-anchor" href="#playing-the-game" aria-hidden="true">#</a></h2><h3 id="ability-scores" tabindex="-1">Ability Scores <a class="header-anchor" href="#ability-scores" aria-hidden="true">#</a></h3><ul><li>Es kann nur ein weiterer Würfel zu jedem d20 addiert werden. Bless, Guidance, Bardic Inspiration, etc stacken nicht.</li></ul><h3 id="resting" tabindex="-1">Resting <a class="header-anchor" href="#resting" aria-hidden="true">#</a></h3><ul><li><em>Gritty Realism (DMG 267)</em></li></ul><p><strong>Short Rest</strong>. Eine Short Rest ist 8 Stunden lang, die meiste Zeit wird geschlafen. Eins bis zwei Stunden Wache halten ist erlaubt.</p><p><strong>Long Rest</strong>. Eine Woche Downtime in einem sicheren Hafen — sprich: 8 aufeinander folgende Short Rests in einem sicheren Hafen, zwischen denen keine Kämpfe oder ähnliche Anstrengungen stattfinden. Die Woche kann zum Entspannen, zu Nachforschungen oder zur Unterhaltung genutzt werden.</p><p><strong>Healer&#39;s Kit</strong>. Ein Character mit Proficiency in Medicine kann eine Verwendung des Healer&#39;s Kits nutzen um Verbündete zu heilen. Für jede so genutzte Verwendung vergehen 10 Minuten und der Verbündete darf einen Hit Die nutzen (so wie während einer Short Rest). Dies kann beliebig oft wiederholt werden.</p><h2 id="adventuring" tabindex="-1">Adventuring <a class="header-anchor" href="#adventuring" aria-hidden="true">#</a></h2><ul><li><strong>Encumbrance (PHB 176)</strong></li><li><em>Sleep (XGE 77)</em></li><li>Level Ups nur nach Abschluss einer Long Rest</li><li><em>Falling (XGE 77)</em></li><li><em>Downtime Activities (XGE 123 ff)</em></li><li><em>Mixing Potions (DMG 140)</em></li><li><em>Tying Knots (XGE 78)</em></li><li><em>More Difficult Identification (DMG 136)</em></li><li><em>Scroll Mishaps (DMG 140)</em></li></ul><h2 id="combat" tabindex="-1">Combat <a class="header-anchor" href="#combat" aria-hidden="true">#</a></h2><ul><li>Action Options: <em>Disarm, Shove, Overrun, Tumble (DMG 271), Identify a Spell (XGE 85)</em></li><li><em>Flanking (DMG 251)</em> — <s>Alternativ +2 anstatt Advant.</s></li><li><em>Playing on a Grid (PHB 192)</em></li><li>Firing into Melee: Target gains Cover (1/2 - 3/4)</li><li>Healing Potions können mittels einer Bonus Action getrunken werden, wenn sie bereits am Mann sind</li><li>Potions einzuflößen benötigt eine Medicine Prof. oder einen DC 10 Check</li><li>Nach der Genesung von 0 HP erhält ein Character +1 Exhaustion Level</li><li>Wird ein Character wiederbelebt verliert er permanent 1 Punkt Constitution (Außnahme Revivify)</li><li><em>Initiative: Speed Factor (DMG 270)</em><ul><li>Slow Spell: Initiative Modifier -2</li><li>Haste Spell: Initiative Modifier +2</li></ul></li></ul>',25),l=[a];function s(o,c,h,d,g,u){return n(),i("div",null,l)}const f=e(r,[["render",s]]);export{p as __pageData,f as default};
